import React, { useState } from 'react';

function Home({ onJoin }) {
  const [nickname, setNickname] = useState('');
  const [gender, setGender] = useState('');
  const [avatar, setAvatar] = useState('');
  const [agree, setAgree] = useState(false);

  const handleJoin = () => {
    if (nickname.trim() && gender && avatar && agree) {
      onJoin({ nickname, gender, avatar });
    } else {
      alert('Please fill all fields and confirm the 18+ policy.');
    }
  };

  return (
    <div className="start-container">
      <h1>Stranger Chat</h1>

      <input
        type="text"
        placeholder="Enter your nickname"
        value={nickname}
        onChange={(e) => setNickname(e.target.value)}
      />

      <div className="gender-select">
        <label>
          <input
            type="radio"
            name="gender"
            value="male"
            onChange={(e) => setGender(e.target.value)}
          />
          Male
        </label>
        <label>
          <input
            type="radio"
            name="gender"
            value="female"
            onChange={(e) => setGender(e.target.value)}
          />
          Female
        </label>
      </div>

      <select value={avatar} onChange={(e) => setAvatar(e.target.value)}>
        <option value="">Choose Avatar</option>
        <option value="ðŸ±">Cat</option>
        <option value="ðŸ¶">Dog</option>
        <option value="ðŸµ">Monkey</option>
        <option value="ðŸ§‘">Human</option>
      </select>

      <div className="policy-check">
        <input
          type="checkbox"
          checked={agree}
          onChange={(e) => setAgree(e.target.checked)}
        />
        <span>I confirm I am 18+ and agree to follow safety policies.</span>
      </div>

      <button onClick={handleJoin}>Start Chatting</button>
    </div>
  );
}

export default Home;
